import base64
import json
import re


def debase64(data: dict or str):
    # 后端服务器插入了一些乱码
    data = data["data"][32:]
    bs64_str = base64.b64decode(data.encode("utf-8")).decode("utf-8", errors='ignore')
    # 正则小概率还是会报错,bs64解出来前面会乱码
    return json.loads(re.findall("{\".*", bs64_str)[0])

if __name__ == '__main__':
    print(debase64(""))